binding.script:

if (getprop("fdm/jsbsim/systems/gauges/PHI/program/route[1]/altitude-hold-ft") <= 1.0)
    setprop("fdm/jsbsim/systems/gauges/PHI/program/route[1]/altitude-hold-ft",getprop("fdm/jsbsim/systems/autopilot/gui/altitude-hold-ft"));
alt = getprop("fdm/jsbsim/systems/gauges/PHI/program/route[1]/altitude-hold-ft");
if (alt > 1.0) {
    if (getprop("fdm/jsbsim/systems/gauges/PHI/program/route[2]/altitude-hold-ft") <= 1.0)
        setprop("fdm/jsbsim/systems/gauges/PHI/program/route[2]/altitude-hold-ft",alt);
    if (getprop("fdm/jsbsim/systems/gauges/PHI/program/route[3]/altitude-hold-ft") <= 1.0)
        setprop("fdm/jsbsim/systems/gauges/PHI/program/route[3]/altitude-hold-ft",alt);
    if (getprop("fdm/jsbsim/systems/gauges/PHI/program/route[4]/altitude-hold-ft") <= 1.0)
        setprop("fdm/jsbsim/systems/gauges/PHI/program/route[4]/altitude-hold-ft",alt);
    if (getprop("fdm/jsbsim/systems/gauges/PHI/program/route[5]/altitude-hold-ft") <= 1.0)
        setprop("fdm/jsbsim/systems/gauges/PHI/program/route[5]/altitude-hold-ft",alt);
}


if (getprop("fdm/jsbsim/systems/gauges/PHI/program/route[2]/altitude-hold-ft") <= 1.0)
    setprop("fdm/jsbsim/systems/gauges/PHI/program/route[2]/altitude-hold-ft",getprop("fdm/jsbsim/systems/autopilot/gui/altitude-hold-ft"));
alt = getprop("fdm/jsbsim/systems/gauges/PHI/program/route[2]/altitude-hold-ft");
if (alt > 1.0) {
    if (getprop("fdm/jsbsim/systems/gauges/PHI/program/route[3]/altitude-hold-ft") <= 1.0)
        setprop("fdm/jsbsim/systems/gauges/PHI/program/route[3]/altitude-hold-ft",alt);
    if (getprop("fdm/jsbsim/systems/gauges/PHI/program/route[4]/altitude-hold-ft") <= 1.0)
        setprop("fdm/jsbsim/systems/gauges/PHI/program/route[4]/altitude-hold-ft",alt);
    if (getprop("fdm/jsbsim/systems/gauges/PHI/program/route[5]/altitude-hold-ft") <= 1.0)
        setprop("fdm/jsbsim/systems/gauges/PHI/program/route[5]/altitude-hold-ft",alt);
}
